import React, { useState } from 'react';
import QrScanner from 'react-qr-scanner';
import axios from 'axios';
import './ScanGoods.css';

function ScanGoods() {
  const [result, setResult] = useState('');
  const [showScanner, setShowScanner] = useState(true);

  const handleScan = async (data) => {
    if (data) {
      setResult(data.text);
      setShowScanner(false);
  
      // Ask to import or export
      const action = window.confirm('Do you want to import or export? Press Ok for import, Export for export.');
      if (action) {
        // Import
        try {
          await axios.post('http://localhost:5000/import', { name: data.text, weight: 1, expiryDate: new Date(), stockAmount: 1 });
          alert('Item imported successfully!');
        } catch (error) {
          console.error('Import failed:', error);
          alert('Failed to import item.');
        }
      } else {
        // Export
        try {
          await axios.post('http://localhost:5000/export', { name: data.text });
          alert('Item exported successfully!');
        } catch (error) {
          console.error('Export failed:', error);
          alert('Failed to export item.');
        }
      }
    }
  };
  

  const handleError = (err) => {
    console.error(err);
  };

  return (
    <div>
      <h2>Scan Goods</h2>
      {showScanner && (
        <div className="qr-scanner-container">
          <QrScanner
            delay={300}
            onError={handleError}
            onScan={handleScan}
            className="qr-scanner"
          />
        </div>
      )}
      {result && <p>Scanned Result: {result}</p>}
    </div>
  );
}

export default ScanGoods;
