import React, { useState } from 'react';
import './ViewGoods.css';

function ViewGoods({ goodsList }) {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value || '');
  };

  // Filter goods based on search term
  const filteredGoods = goodsList.filter(good =>
    good.item && good.item.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h2>Available Goods</h2>
      <input
        type="text"
        placeholder="Search goods..."
        value={searchTerm}
        onChange={handleSearchChange}
        className="search-input"
      />
      <table>
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Quantity</th>
            <th>Expiry Date</th>
            <th>Weight</th>
            <th>Date Scanned</th>
            <th>Added</th>
          </tr>
        </thead>
        <tbody>
          {filteredGoods.map((good, index) => (
            <tr key={index}>
              <td>{good.item}</td>
              <td>{good.quantity}</td>
              <td>{good.expiryDate}</td>
              <td>{good.weight}</td>
              <td>{good.date}</td>
              <td>{good.added ? 'Yes' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ViewGoods;
