import React, { useState } from 'react';
import QrScanner from 'react-qr-scanner';
import './HomePage.css';

function HomePage() {
  const [result, setResult] = useState('');
  const [showScanner, setShowScanner] = useState(false);
  const [showGoods, setShowGoods] = useState(false);
  const [goodsList, setGoodsList] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  const handleScan = (data) => {
    if (data) {
      setResult(data.text);
      setShowScanner(false);

      // Ask to import or export
      const action = window.confirm('Do you want to import or export? Press OK for import, Cancel for export.');
      if (action) {
        handleImport(data.text);
      } else {
        handleExport(data.text);
      }
    }
  };

  const handleError = (err) => {
    console.error(err);
  };

  const handleImport = (item) => {
    const parsedData = JSON.parse(item);
    // Add the scanned item to the goods list
    setGoodsList(prevList => [...prevList, { ...parsedData, date: new Date().toLocaleDateString(), added: true }]);
  };

  const handleExport = (item) => {
    // Remove the scanned item from the goods list
    setGoodsList(prevList => prevList.filter(good => good.name !== item));
  };

  const handleViewGoods = () => {
    setShowGoods(true);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredGoods = goodsList.filter(good =>
    good.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h2>Warehouse Management</h2>
      <button onClick={() => setShowScanner(true)}>Scan Goods</button>
      <button onClick={handleViewGoods}>View Goods</button>

      {showScanner && (
        <div className="qr-scanner-container">
          <QrScanner
            delay={300}
            onError={handleError}
            onScan={handleScan}
            className="qr-scanner"
          />
        </div>
      )}

      {showGoods && (
        <div className="goods-list">
          <h3>Available Goods</h3>
          <input
            type="text"
            placeholder="Search goods..."
            value={searchTerm}
            onChange={handleSearchChange}
            className="search-input"
          />
          <table>
            <thead>
              <tr>
                {/* Create table headers for each key in goodsList */}
                {Object.keys(goodsList[0]).map((key, index) => (
                  <th key={index}>{key}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {filteredGoods.map((good, index) => (
                <tr key={index}>
                  {/* Display each value of the good object in a separate cell */}
                  {Object.values(good).map((value, ind) => (
                    <td key={ind}>{value}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {result && <p>Scanned Result: {result}</p>}
    </div>
  );
}

export default HomePage;
