import React, { useState } from 'react';
import QrScanner from 'react-qr-scanner';
import './HomePage.css';

function HomePage() {
  const [result, setResult] = useState('');
  const [showScanner, setShowScanner] = useState(false);
  const [showGoods, setShowGoods] = useState(false);
  const [goodsList, setGoodsList] = useState([]); // List of scanned goods

  const handleScan = (data) => {
    if (data) {
      setResult(data.text);
      setShowScanner(false); // Hide scanner after scanning

      // Add scanned item to the goods list
      setGoodsList(prevList => [...prevList, { name: data.text, date: new Date().toLocaleDateString() }]);

      // Ask to import or export here
      const action = window.confirm('Do you want to import or export? Press OK for import, Cancel for export.');
      if (action) {
        handleImport();
      } else {
        handleExport();
      }
    }
  };

  const handleError = (err) => {
    console.error(err);
  };

  const handleImport = () => {
    console.log('Import clicked with result:', result);
    // Implement your import logic here
  };

  const handleExport = () => {
    console.log('Export clicked with result:', result);
    // Implement your export logic here
  };

  const handleViewGoods = () => {
    setShowGoods(true);
  };

  return (
    <div>
      <h2>Warehouse Management</h2>
      <button onClick={() => setShowScanner(true)}>Scan Goods</button>
      <button onClick={handleViewGoods}>View Goods</button>

      {showScanner && (
        <div className="qr-scanner-container">
          <QrScanner
            delay={300}
            onError={handleError}
            onScan={handleScan}
            className="qr-scanner"
          />
        </div>
      )}

      {showGoods && (
        <div className="goods-list">
          <h3>Available Goods</h3>
          <table>
            <thead>
              <tr>
                <th>Item Name</th>
                <th>Date Scanned</th>
              </tr>
            </thead>
            <tbody>
              {goodsList.map((good, index) => (
                <tr key={index}>
                  <td>{good.name}</td>
                  <td>{good.date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {result && <p>Scanned Result: {result}</p>}
    </div>
  );
}

export default HomePage;
