import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ViewGoods.css';

function ViewGoods() {
  const [goodsList, setGoodsList] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGoods = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/goods');
        const formattedData = response.data.map(good => ({
          name: good.item,  // Assuming the API returns `item` for name
          weight: good.weight,
          expiryDate: new Date(good.expiryDate).toLocaleDateString(),  // Format date
          quantity: good.quantity,
          dateScanned: new Date(good.dateScanned).toLocaleDateString() // Format date if available
        }));
        setGoodsList(formattedData);
      } catch (err) {
        console.error('Error fetching goods:', err);
        setError('Failed to fetch goods');
      }
    };
    fetchGoods();
  }, []);

  return (
    <div className="view-goods-container">
      <h2>Available Goods</h2>
      {error && <p className="error-message">{error}</p>}
      <table className="goods-table">
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Weight</th>
            <th>Expiry Date</th>
            <th>Quantity</th>
            <th>Date Scanned</th>
          </tr>
        </thead>
        <tbody>
          {goodsList.map((good, index) => (
            <tr key={index}>
              <td>{good.name}</td>
              <td>{good.weight}</td>
              <td>{good.expiryDate}</td>
              <td>{good.quantity}</td>
              <td>{good.dateScanned || 'N/A'}</td>  {/* Fallback for dateScanned */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ViewGoods;
