// src/components/HomePage.js
import React, { useState } from 'react';
import QrReader from 'react-qr-reader';

function HomePage() {
  const [result, setResult] = useState('');
  const [action, setAction] = useState('');

  const handleScan = data => {
    if (data) {
      setResult(data);
      setAction(''); // Reset action on new scan
    }
  };

  const handleError = err => {
    console.error(err);
  };

  const handleAction = (actionType) => {
    setAction(actionType);
    const url = `http://localhost:5000/${actionType.toLowerCase()}`;
    fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ goods: result }),
    })
    .then(response => response.json())
    .then(data => console.log(data))
    .catch(err => console.error('Error:', err));
  };

  return (
    <div>
      <h2>Warehouse Management</h2>
      <QrReader
        delay={300}
        onError={handleError}
        onScan={handleScan}
        style={{ width: '100%' }}
      />
      <p>{result}</p>
      {result && (
        <>
          <button onClick={() => handleAction('Import')}>Import</button>
          <button onClick={() => handleAction('Export')}>Export</button>
        </>
      )}
    </div>
  );
}

export default HomePage;
